    private void jButton_AdicionarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_AdicionarActionPerformed

        try {
            if (validarCampos()) {
                if (preencherObjeto()) {
                    //Inclusao no banco
                    PessoasData cont = new PessoasData();
                    if (cont.incluir(obj)) {

                        JOptionPane.showMessageDialog(this, "Contato salvo!");
                        limparCampos();
                    } else {
                        JOptionPane.showMessageDialog(this, "NÃ£o foi possivel adicionar o contato!");

                    }
                }

            }
        } catch (Exception erro) {
            JOptionPane.showMessageDialog(this, "Erro: " + erro.getMessage());
        }

    }//GEN-LAST:event_jButton_AdicionarActionPerformed

