private boolean validarCampos() {
        if (jTextField_Nome.getText().equals("")) {
            JOptionPane.showMessageDialog(rootPane, "Digite o Nome");
            jTextField_Nome.requestFocus();
            return false;
        }
        if (jTextField_Telefone.getText().equals("")) {
            JOptionPane.showMessageDialog(rootPane, "Digite o Telefone");
            jTextField_Telefone.requestFocus();
            return false;
        }
        return true;
    }

    private boolean preencherObjeto() throws Exception {
        obj = new Pessoa();
        //obj.setId
        obj.setNome(jTextField_Nome.getText());
        obj.setTelefone(jTextField_Telefone.getText());
        return true;
    }

    private void limparCampos() {
        jTextField_Nome.setText(null);
        jTextField_Telefone.setText(null);

    }

